import { Box } from "@mui/material";

export const InputCard = ({ children }) => {
  return (
    <Box elevation={3} style={baseStyle}>
      {children}
    </Box>
  );
};

const baseStyle = {
  box: {
    borderRadius: 25,
    backgroundColor: "white",
    width: "42vw",
    //height: "88vh",
    margin: "auto",
    marginTop: "1.5vh",
    marginBottom: "1.5vh",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    gap: "1vh",
    padding: "5vh",
  },
};
